
public class dadosinstrucoesdeatribuicao02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 5,j,k;
		j = --i;
		k = j++;
		i += 10;
		System.out.println("o valor de i eh igual a :" + i);
		System.out.println(" o valor de j eh igual a :" + j);
		System.out.println("o valor de k eh igual a:" + k);

	}

}
